using System;
using System.Drawing;
using System.IO;
using System.Linq;
using System.Windows.Forms;

namespace ClassicSlidePuzzleGame
{
    public partial class FormEasy : Form
    {
        readonly int[,] pieces = new int[3, 3];
        readonly Image[,] subImg = new Image[3, 3];
        Point emptyPoint = new Point(2, 2);
        Button btnEmpty;
        Button btnFill;
        int x, y;
        readonly Random rand = new Random();
        Bitmap bmp = new Bitmap(Base64StringToBitmap(""), 345, 345);
        Bitmap bmpEmpty = new Bitmap(Base64StringToBitmap(""), 115, 115);
        public FormEasy()
        {
            InitializeComponent();
        }
        private void ChangeState(object sender, EventArgs e)
        {
            Button btn = (Button)sender;
            int value;
            value = int.Parse(btn.Name.Substring(6, 1));
            x = (value / 3);
            y = (value % 3);
            if (emptyPoint.X == x && emptyPoint.Y != y)
            {
                if (emptyPoint.Y > y)
                {
                    for (int i = emptyPoint.Y; i > y; i--)
                    {
                        pieces[emptyPoint.X, i] = pieces[emptyPoint.X, i - 1];
                        subImg[emptyPoint.X, i] = subImg[emptyPoint.X, i - 1];
                        string emptyButtonName = "button" + ((i + 1 + emptyPoint.X * 3) - 1);
                        btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                        btnEmpty.BackgroundImage = subImg[emptyPoint.X, i];
                    }
                    pieces[x, y] = 0;
                    subImg[x, y] = bmpEmpty;
                    btn.BackgroundImage = bmpEmpty;
                    emptyPoint.X = x;
                    emptyPoint.Y = y;
                }
                else
                {
                    for (int i = emptyPoint.Y; i < y; i++)
                    {
                        pieces[emptyPoint.X, i] = pieces[emptyPoint.X, i + 1];
                        subImg[emptyPoint.X, i] = subImg[emptyPoint.X, i + 1];
                        string emptyButtonName = "button" + ((i + 1 + emptyPoint.X * 3) - 1);
                        btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                        btnEmpty.BackgroundImage = subImg[emptyPoint.X, i];
                    }
                    pieces[x, y] = 0;
                    subImg[x, y] = bmpEmpty;
                    btn.BackgroundImage = bmpEmpty;
                    emptyPoint.X = x;
                    emptyPoint.Y = y;
                }
            }
            else if (emptyPoint.X != x && emptyPoint.Y == y)
            {
                if (emptyPoint.X > x)
                {
                    for (int i = emptyPoint.X; i > x; i--)
                    {
                        pieces[i, emptyPoint.Y] = pieces[i - 1, emptyPoint.Y];
                        subImg[i, emptyPoint.Y] = subImg[i - 1, emptyPoint.Y];
                        string emptyButtonName = "button" + ((emptyPoint.Y + 1 + i * 3) - 1);
                        btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                        btnEmpty.BackgroundImage = subImg[i, emptyPoint.Y];
                    }
                    pieces[x, y] = 0;
                    subImg[x, y] = bmpEmpty;
                    btn.BackgroundImage = bmpEmpty;
                    emptyPoint.X = x;
                    emptyPoint.Y = y;
                }
                else
                {
                    for (int i = emptyPoint.X; i < x; i++)
                    {
                        pieces[i, emptyPoint.Y] = pieces[i + 1, emptyPoint.Y];
                        subImg[i, emptyPoint.Y] = subImg[i + 1, emptyPoint.Y];
                        string emptyButtonName = "button" + ((emptyPoint.Y + 1 + i * 3) - 1);
                        btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                        btnEmpty.BackgroundImage = subImg[i, emptyPoint.Y];
                    }
                    pieces[x, y] = 0;
                    subImg[x, y] = bmpEmpty;
                    btn.BackgroundImage = bmpEmpty;
                    emptyPoint.X = x;
                    emptyPoint.Y = y;
                }
            }
            if (CheckFinished()) MessageBox.Show("You win");
        }

        private bool CheckFinished()
        {
            int min = pieces[0, 0];
            for (int row = 0; row < 3; row++)
            {
                for (int collumn = 0; collumn < 3; collumn++)
                {
                    if (pieces[row, collumn] < min && (row != 2 || collumn != 2)) return false;
                    else
                    {
                        min = pieces[row, collumn];
                    }
                }
            }
            return true;
        }

        private void DoUp()
        {
            if (emptyPoint.X != 2)
            {
                pieces[emptyPoint.X, emptyPoint.Y] = pieces[emptyPoint.X + 1, emptyPoint.Y];
                pieces[emptyPoint.X + 1, emptyPoint.Y] = 0;
                subImg[emptyPoint.X, emptyPoint.Y] = subImg[emptyPoint.X + 1, emptyPoint.Y];
                subImg[emptyPoint.X + 1, emptyPoint.Y] = bmpEmpty;
                string emptyButtonName = "button" + (emptyPoint.Y + 1 + emptyPoint.X * 3 - 1);
                btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                string fillButtonName = "button" + (emptyPoint.Y + 1 + (emptyPoint.X + 1) * 3 - 1);
                btnFill = Controls.Find(fillButtonName, true).FirstOrDefault() as Button;
                btnFill.BackgroundImage = bmpEmpty;
                btnEmpty.BackgroundImage = subImg[emptyPoint.X, emptyPoint.Y];
                emptyPoint.X += 1;
            }
            else DoDown();
        }
        private void DoDown()
        {
            if (emptyPoint.X != 0)
            {
                pieces[emptyPoint.X, emptyPoint.Y] = pieces[emptyPoint.X - 1, emptyPoint.Y];
                pieces[emptyPoint.X - 1, emptyPoint.Y] = 0;
                subImg[emptyPoint.X, emptyPoint.Y] = subImg[emptyPoint.X - 1, emptyPoint.Y];
                subImg[emptyPoint.X - 1, emptyPoint.Y] = bmpEmpty;
                string emptyButtonName = "button" + (emptyPoint.Y + 1 + emptyPoint.X * 3 - 1);
                btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                string fillButtonName = "button" + (emptyPoint.Y + 1 + (emptyPoint.X - 1) * 3 - 1);
                btnFill = Controls.Find(fillButtonName, true).FirstOrDefault() as Button;
                btnFill.BackgroundImage = bmpEmpty;
                btnEmpty.BackgroundImage = subImg[emptyPoint.X, emptyPoint.Y];
                emptyPoint.X -= 1;
            }
            else DoUp();
        }
        private void DoLeft()
        {
            if (emptyPoint.Y != 2)
            {
                pieces[emptyPoint.X, emptyPoint.Y] = pieces[emptyPoint.X, emptyPoint.Y + 1];
                pieces[emptyPoint.X, emptyPoint.Y + 1] = 0;
                subImg[emptyPoint.X, emptyPoint.Y] = subImg[emptyPoint.X, emptyPoint.Y + 1];
                subImg[emptyPoint.X, emptyPoint.Y + 1] = bmpEmpty;
                string emptyButtonName = "button" + (emptyPoint.Y + 1 + emptyPoint.X * 3 - 1);
                btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                string fillButtonName = "button" + (emptyPoint.Y + 2 + emptyPoint.X * 3 - 1);
                btnFill = Controls.Find(fillButtonName, true).FirstOrDefault() as Button;
                btnFill.BackgroundImage = bmpEmpty;
                btnEmpty.BackgroundImage = subImg[emptyPoint.X, emptyPoint.Y];
                emptyPoint.Y += 1;
            }
            else DoRight();
        }
        private void DoRight()
        {
            if (emptyPoint.Y != 0)
            {
                pieces[emptyPoint.X, emptyPoint.Y] = pieces[emptyPoint.X, emptyPoint.Y - 1];
                pieces[emptyPoint.X, emptyPoint.Y - 1] = 0;
                subImg[emptyPoint.X, emptyPoint.Y] = subImg[emptyPoint.X, emptyPoint.Y - 1];
                subImg[emptyPoint.X, emptyPoint.Y - 1] = bmpEmpty;
                string emptyButtonName = "button" + (emptyPoint.Y + 1 + emptyPoint.X * 3 - 1);
                btnEmpty = Controls.Find(emptyButtonName, true).FirstOrDefault() as Button;
                string fillButtonName = "button" + (emptyPoint.Y + emptyPoint.X * 3 - 1);
                btnFill = Controls.Find(fillButtonName, true).FirstOrDefault() as Button;
                btnFill.BackgroundImage = bmpEmpty;
                btnEmpty.BackgroundImage = subImg[emptyPoint.X, emptyPoint.Y];
                emptyPoint.Y -= 1;
            }
            else DoLeft();
        }

        private void ButtonScramble_Click(object sender, EventArgs e)
        {
            Scramble();
        }

        private void FormEasy_Load(object sender, EventArgs e)
        {
            ReloadImage();
            for (int i = 0; i < 9; i++)
            {
                string s = "button" + i;
                Button btn = Controls.Find(s, true).FirstOrDefault() as Button;
                btn.Click += ChangeState;
                btn.FlatAppearance.BorderSize = 0;
            }
        }
        private void ReloadImage()
        {
            emptyPoint = new Point(2, 2);
            pictureBox1.Image = bmp;
            button8.Text = "";
            button8.BackColor = Color.White;
            int startX = 0, startY = 0;
            for (int row = 0; row < 3; row++)
            {
                for (int collumn = 0; collumn < 3; collumn++)
                {
                    pieces[row, collumn] = (collumn + 1 + row * 3);

                    Rectangle cloneRect = new Rectangle(startX, startY, 115, 115);
                    System.Drawing.Imaging.PixelFormat format = bmp.PixelFormat;
                    Bitmap cloneBitmap = bmp.Clone(cloneRect, format);
                    subImg[row, collumn] = cloneBitmap;
                    string s = "button" + (collumn + 1 + row * 3 - 1);
                    Button btn = this.Controls.Find(s, true).FirstOrDefault() as Button;
                    btn.BackgroundImage = cloneBitmap;
                    startX += 115;
                    if (startX == 345)
                    {
                        startY += 115;
                        startX = 0;
                    }
                }
            }
            pieces[2, 2] = 0;
            subImg[2, 2] = bmpEmpty;
            button8.BackgroundImage = bmpEmpty;
            Scramble();
        }
        private void ButtonChangeImage_Click(object sender, EventArgs e)
        {
            OpenFileDialog open = new OpenFileDialog
            {
                // image filters  
                Filter = "Image Files(*.jpg; .jpeg; .gif; .bmp; .png)|*.jpg; .jpeg; .gif; *.bmp ;*.png"
            };
            if (open.ShowDialog() == DialogResult.OK)
            {
                // image file path  
                bmp = new Bitmap(new Bitmap(open.FileName), 350, 350);
                bmpEmpty = new Bitmap(Base64StringToBitmap(""), 115, 115);
                ReloadImage();
            }
        }

        private void Scramble()
        {
            for (int i = 0; i < 800; i++)
            {
                int num = rand.Next(0, 4);
                switch (num)
                {
                    case 0:
                        DoUp();
                        break;
                    case 1:
                        DoDown();
                        break;
                    case 2:
                        DoLeft();
                        break;
                    case 3:
                        DoRight();
                        break;
                }
            }
        }

        private static Bitmap Base64StringToBitmap(string base64String)
        {
            byte[] byteBuffer = Convert.FromBase64String(base64String);
            MemoryStream memoryStream = new MemoryStream(byteBuffer)
            {
                Position = 0
            };
            Bitmap bmpReturn = (Bitmap)Image.FromStream(memoryStream);
            memoryStream.Close();
            return bmpReturn;
        }
    }
}